contracts/inchbyinchBot.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./interfaces/I1inchLOP.sol";
import "./interfaces/IOrderManager.sol";
import "./interfaces/IOracleAdapter.sol";

/**
 * @title inchbyinchBot
 * @notice Core trading bot for inchbyinch ladder strategies
 * @dev Handles ladder order placement, reposting, and strategy execution
 */
contract inchbyinchBot is Ownable, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    // Strategy types
    uint256 public constant STRATEGY_BUY_LADDER = 0;
    uint256 public constant STRATEGY_SELL_LADDER = 1;
    uint256 public constant STRATEGY_BUY_SELL = 2;

    // Repost modes
    uint256 public constant REPOST_SAME_PRICE = 0;
    uint256 public constant REPOST_NEXT_PRICE = 1;
    uint256 public constant REPOST_SKIP = 2;

    // Configuration
    uint256 public constant MAX_ORDERS = 50;
    uint256 public constant MAX_ORDER_SIZE = 1000 ether;
    uint256 public constant MIN_ORDER_SIZE = 0.001 ether;
    uint256 public constant MAX_SPACING = 1000; // 1000% max spacing
    uint256 public constant MIN_SPACING = 1; // 1% min spacing

    // State
    struct Strategy {
        address makerAsset;
        address takerAsset;
        uint256 startPrice;
        uint256 spacing;
        uint256 orderSize;
        uint256 numOrders;
        uint256 strategyType;
        uint256 repostMode;
        uint256 budget;
        uint256 stopLoss;
        uint256 takeProfit;
        uint256 expiryTime;
        bool isActive;
        uint256 currentOrderIndex;
        uint256 totalFilled;
        uint256 totalSpent;
    }

    struct Order {
        bytes32 orderHash;
        uint256 price;
        uint256 orderIndex;
        bool isActive;
        uint256 createdAt;
    }

    // Storage
    Strategy public strategy;
    mapping(uint256 => Order) public orders;
    mapping(bytes32 => uint256) public orderIndexByHash;

    // External contracts
    I1inchLOP public lop;
    IOrderManager public orderManager;
    IOracleAdapter public oracleAdapter;

    // Events
    event StrategyCreated(
        address indexed makerAsset,
        address indexed takerAsset,
        uint256 startPrice,
        uint256 spacing,
        uint256 orderSize,
        uint256 numOrders,
        uint256 strategyType
    );

    event OrderPlaced(
        bytes32 indexed orderHash,
        uint256 indexed orderIndex,
        uint256 price,
        uint256 orderSize
    );

    event OrderFilled(
        bytes32 indexed orderHash,
        uint256 indexed orderIndex,
        uint256 filledAmount,
        uint256 price
    );

    event OrderReposted(
        bytes32 indexed oldOrderHash,
        bytes32 indexed newOrderHash,
        uint256 newPrice,
        uint256 orderIndex
    );

    event StrategyCompleted(
        uint256 totalFilled,
        uint256 totalSpent,
        uint256 profit
    );

    event StrategyCancelled();

    // Errors
    error InvalidStrategy();
    error InvalidOrderSize();
    error InvalidSpacing();
    error InvalidPrice();
    error InvalidBudget();
    error InvalidStopLoss();
    error InvalidTakeProfit();
    error InvalidExpiry();
    error StrategyAlreadyActive();
    error StrategyNotActive();
    error OrderNotFound();
    error OrderAlreadyExists();
    error OrderAlreadyFilled();
    error OrderAlreadyCanceled();
    error InsufficientBalance();
    error ExceedsBudget();
    error StopLossTriggered();
    error TakeProfitTriggered();
    error StrategyExpired();
    error UnauthorizedCaller();
    error ZeroAddress();
    error ZeroAmount();

    // Modifiers
    modifier onlyAuthorized() {
        if (msg.sender != owner() && !orderManager.isBotAuthorized(address(this))) revert UnauthorizedCaller();
        _;
    }

    modifier strategyActive() {
        if (!strategy.isActive) revert StrategyNotActive();
        _;
    }

    modifier validAddress(address addr) {
        if (addr == address(0)) revert ZeroAddress();
        _;
    }

    modifier validAmount(uint256 amount) {
        if (amount == 0) revert ZeroAmount();
        _;
    }

    modifier validPrice(uint256 price) {
        if (price == 0) revert InvalidPrice();
        _;
    }

    modifier validSpacing(uint256 spacing) {
        if (spacing < MIN_SPACING || spacing > MAX_SPACING) revert InvalidSpacing();
        _;
    }

    modifier validOrderSize(uint256 size) {
        if (size < MIN_ORDER_SIZE || size > MAX_ORDER_SIZE) revert InvalidOrderSize();
        _;
    }

    constructor() Ownable(msg.sender) {
        // Implementation contract - will be initialized via proxy
    }

    /**
     * @notice Initializes the bot with external contracts
     * @param _lop The LOP contract address
     * @param _orderManager The order manager address
     * @param _oracleAdapter The oracle adapter address
     */
    function initialize(
        address _lop,
        address _orderManager,
        address _oracleAdapter
    ) external validAddress(_lop) validAddress(_orderManager) validAddress(_oracleAdapter) {
        // Only allow initialization once
        require(address(lop) == address(0), "Already initialized");

        // Set external contracts
        lop = I1inchLOP(_lop);
        orderManager = IOrderManager(_orderManager);
        oracleAdapter = IOracleAdapter(_oracleAdapter);


    }

    /**
     * @notice Creates a new ladder strategy
     * @param makerAsset The asset being sold
     * @param takerAsset The asset being bought
     * @param startPrice The starting price
     * @param spacing The price spacing between orders
     * @param orderSize The size of each order
     * @param numOrders The number of orders to place
     * @param strategyType The type of strategy
     * @param repostMode The repost mode
     * @param budget The maximum budget
     * @param stopLoss The stop loss price
     * @param takeProfit The take profit price
     * @param expiryTime The expiry time
     */
    function createStrategy(
        address makerAsset,
        address takerAsset,
        uint256 startPrice,
        uint256 spacing,
        uint256 orderSize,
        uint256 numOrders,
        uint256 strategyType,
        uint256 repostMode,
        uint256 budget,
        uint256 stopLoss,
        uint256 takeProfit,
        uint256 expiryTime
    ) external onlyOwner validAddress(makerAsset) validAddress(takerAsset) validPrice(startPrice) validSpacing(spacing) validOrderSize(orderSize) {
        // Validate strategy parameters
        if (strategy.isActive) revert StrategyAlreadyActive();
        if (numOrders == 0 || numOrders > MAX_ORDERS) revert InvalidStrategy();
        if (strategyType > 2) revert InvalidStrategy();
        if (repostMode > 2) revert InvalidStrategy();
        if (budget == 0) revert InvalidBudget();
        if (expiryTime <= block.timestamp) revert InvalidExpiry();

        // Validate stop loss and take profit
        if (stopLoss > 0 && stopLoss >= startPrice) revert InvalidStopLoss();
        if (takeProfit > 0 && takeProfit <= startPrice) revert InvalidTakeProfit();

        // Check token balances
        uint256 makerBalance = IERC20(makerAsset).balanceOf(address(this));
        uint256 takerBalance = IERC20(takerAsset).balanceOf(address(this));

        if (strategyType == STRATEGY_BUY_LADDER) {
            if (takerBalance < budget) revert InsufficientBalance();
        } else if (strategyType == STRATEGY_SELL_LADDER) {
            if (makerBalance < (orderSize * numOrders)) revert InsufficientBalance();
        } else if (strategyType == STRATEGY_BUY_SELL) {
            if (takerBalance < budget) revert InsufficientBalance();
        }

        // Create strategy
        strategy = Strategy({
            makerAsset: makerAsset,
            takerAsset: takerAsset,
            startPrice: startPrice,
            spacing: spacing,
            orderSize: orderSize,
            numOrders: numOrders,
            strategyType: strategyType,
            repostMode: repostMode,
            budget: budget,
            stopLoss: stopLoss,
            takeProfit: takeProfit,
            expiryTime: expiryTime,
            isActive: true,
            currentOrderIndex: 0,
            totalFilled: 0,
            totalSpent: 0
        });

        // Register strategy with order manager
        orderManager.createStrategy(address(this), owner(), strategyType);

        emit StrategyCreated(
            makerAsset,
            takerAsset,
            startPrice,
            spacing,
            orderSize,
            numOrders,
            strategyType
        );
    }

    /**
     * @notice Places ladder orders
     */
    function placeLadderOrders() external onlyOwner strategyActive {
        uint256 currentPrice = _getCurrentPrice();

        // Check stop conditions
        if (strategy.stopLoss > 0 && currentPrice <= strategy.stopLoss) revert StopLossTriggered();
        if (strategy.takeProfit > 0 && currentPrice >= strategy.takeProfit) revert TakeProfitTriggered();
        if (block.timestamp > strategy.expiryTime) revert StrategyExpired();

        // Place orders based on strategy type
        if (strategy.strategyType == STRATEGY_BUY_LADDER) {
            _placeBuyLadderOrders();
        } else if (strategy.strategyType == STRATEGY_SELL_LADDER) {
            _placeSellLadderOrders();
        } else if (strategy.strategyType == STRATEGY_BUY_SELL) {
            _placeBuySellOrders();
        }
    }

    /**
     * @notice Handles order fill callback from LOP
     * @param orderHash The order hash
     * @param filledAmount The amount filled
     * @param remainingAmount The remaining amount
     */
    function handleOrderFill(
        bytes32 orderHash,
        uint256 filledAmount,
        uint256 remainingAmount
    ) external onlyAuthorized {
        uint256 orderIndex = orderIndexByHash[orderHash];
        if (orderIndex == 0) revert OrderNotFound();

        Order storage order = orders[orderIndex];
        if (!order.isActive) revert OrderAlreadyCanceled();

        // Update order
        order.isActive = remainingAmount > 0;

        // Update strategy
        strategy.totalFilled += filledAmount;
        strategy.totalSpent += (filledAmount * order.price) / 1e18;

        // Update order manager
        orderManager.updateOrderFill(orderHash, filledAmount, remainingAmount);

        emit OrderFilled(orderHash, orderIndex, filledAmount, order.price);

        // Handle reposting
        if (remainingAmount == 0) {
            _handleOrderRepost(orderIndex);
        }
    }

    /**
     * @notice Cancels all active orders
     */
    function cancelAllOrders() external onlyOwner strategyActive {
        for (uint256 i = 1; i <= strategy.currentOrderIndex; i++) {
            Order storage order = orders[i];
            if (order.isActive) {
                _cancelOrder(i);
            }
        }

        strategy.isActive = false;
        emit StrategyCancelled();
    }

    /**
     * @notice Cancels a specific order
     * @param orderIndex The order index
     */
    function cancelOrder(uint256 orderIndex) external onlyOwner strategyActive {
        if (orderIndex == 0 || orderIndex > strategy.currentOrderIndex) revert OrderNotFound();

        Order storage order = orders[orderIndex];
        if (!order.isActive) revert OrderAlreadyCanceled();

        _cancelOrder(orderIndex);
    }

    /**
     * @notice Withdraws tokens from the bot
     * @param token The token address
     * @param amount The amount to withdraw
     */
    function withdrawTokens(address token, uint256 amount) external onlyOwner {
        IERC20(token).safeTransfer(owner(), amount);
    }

    /**
     * @notice Gets order information
     * @param orderIndex The order index
     * @return order The order information
     */
    function getOrder(uint256 orderIndex) external view returns (Order memory order) {
        if (orderIndex == 0 || orderIndex > strategy.currentOrderIndex) revert OrderNotFound();
        return orders[orderIndex];
    }

    /**
     * @notice Gets all active orders
     * @return activeOrders Array of active order indices
     */
    function getActiveOrders() external view returns (uint256[] memory activeOrders) {
        uint256 count = 0;
        for (uint256 i = 1; i <= strategy.currentOrderIndex; i++) {
            if (orders[i].isActive) count++;
        }

        activeOrders = new uint256[](count);
        uint256 index = 0;
        for (uint256 i = 1; i <= strategy.currentOrderIndex; i++) {
            if (orders[i].isActive) {
                activeOrders[index] = i;
                index++;
            }
        }
    }

    /**
     * @notice Gets strategy performance
     * @return totalFilled Total amount filled
     * @return totalSpent Total amount spent
     * @return profit Profit/loss
     */
    function getStrategyPerformance() external view returns (uint256 totalFilled, uint256 totalSpent, int256 profit) {
        totalFilled = strategy.totalFilled;
        totalSpent = strategy.totalSpent;

        if (strategy.strategyType == STRATEGY_BUY_LADDER) {
            uint256 currentValue = (totalFilled * _getCurrentPrice()) / 1e18;
            profit = int256(currentValue) - int256(totalSpent);
        } else if (strategy.strategyType == STRATEGY_SELL_LADDER) {
            uint256 currentValue = (totalFilled * _getCurrentPrice()) / 1e18;
            profit = int256(totalSpent) - int256(currentValue);
        }
    }

    /**
     * @notice Pauses the bot
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice Unpauses the bot
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @notice Places buy ladder orders
     */
    function _placeBuyLadderOrders() private {
        uint256 currentPrice = _getCurrentPrice();
        uint256 dynamicSpacing = _getDynamicSpacing();

        for (uint256 i = 0; i < strategy.numOrders; i++) {
            uint256 orderPrice = currentPrice - (i * dynamicSpacing);
            if (orderPrice <= 0) break;

            _placeOrder(orderPrice, strategy.orderSize, true);
        }
    }

    /**
     * @notice Places sell ladder orders
     */
    function _placeSellLadderOrders() private {
        uint256 currentPrice = _getCurrentPrice();
        uint256 dynamicSpacing = _getDynamicSpacing();

        for (uint256 i = 0; i < strategy.numOrders; i++) {
            uint256 orderPrice = currentPrice + (i * dynamicSpacing);
            _placeOrder(orderPrice, strategy.orderSize, false);
        }
    }

    /**
     * @notice Places buy and sell orders
     */
    function _placeBuySellOrders() private {
        uint256 currentPrice = _getCurrentPrice();
        uint256 dynamicSpacing = _getDynamicSpacing();

        // Place buy orders below current price
        for (uint256 i = 1; i <= strategy.numOrders / 2; i++) {
            uint256 orderPrice = currentPrice - (i * dynamicSpacing);
            if (orderPrice <= 0) break;
            _placeOrder(orderPrice, strategy.orderSize, true);
        }

        // Place sell orders above current price
        for (uint256 i = 1; i <= strategy.numOrders / 2; i++) {
            uint256 orderPrice = currentPrice + (i * dynamicSpacing);
            _placeOrder(orderPrice, strategy.orderSize, false);
        }
    }

    /**
     * @notice Places a single order
     * @param price The order price
     * @param size The order size
     * @param isBuy Whether this is a buy order
     */
    function _placeOrder(uint256 price, uint256 size, bool isBuy) private {
        strategy.currentOrderIndex++;

        // Create LOP order
        I1inchLOP.Order memory lopOrder = I1inchLOP.Order({
            salt: uint256(keccak256(abi.encodePacked(block.timestamp, strategy.currentOrderIndex))),
            makerAsset: isBuy ? strategy.takerAsset : strategy.makerAsset,
            takerAsset: isBuy ? strategy.makerAsset : strategy.takerAsset,
            maker: address(this),
            receiver: address(this),
            allowedSender: address(0),
            makingAmount: size,
            takingAmount: (size * price) / 1e18,
            offsets: 0,
            interactions: abi.encodeWithSignature("handleOrderFill(bytes32,uint256,uint256)", bytes32(0), uint256(0), uint256(0))
        });

        bytes32 orderHash = lop.getOrderHash(lopOrder);

        // Store order
        orders[strategy.currentOrderIndex] = Order({
            orderHash: orderHash,
            price: price,
            orderIndex: strategy.currentOrderIndex,
            isActive: true,
            createdAt: block.timestamp
        });

        orderIndexByHash[orderHash] = strategy.currentOrderIndex;

        // Register with order manager
        orderManager.registerOrder(
            orderHash,
            address(this),
            lopOrder.makerAsset,
            lopOrder.takerAsset,
            lopOrder.makingAmount,
            lopOrder.takingAmount
        );

        emit OrderPlaced(orderHash, strategy.currentOrderIndex, price, size);
    }

    /**
     * @notice Handles order reposting
     * @param orderIndex The order index
     */
    function _handleOrderRepost(uint256 orderIndex) private {
        if (strategy.repostMode == REPOST_SKIP) return;

        Order storage order = orders[orderIndex];
        uint256 newPrice = order.price;

        if (strategy.repostMode == REPOST_NEXT_PRICE) {
            if (strategy.strategyType == STRATEGY_BUY_LADDER) {
                newPrice = order.price - _getDynamicSpacing();
            } else if (strategy.strategyType == STRATEGY_SELL_LADDER) {
                newPrice = order.price + _getDynamicSpacing();
            }
        }

        if (newPrice > 0) {
            _placeOrder(newPrice, strategy.orderSize, strategy.strategyType == STRATEGY_BUY_LADDER);

            emit OrderReposted(
                order.orderHash,
                orders[strategy.currentOrderIndex].orderHash,
                newPrice,
                strategy.currentOrderIndex
            );
        }
    }

    /**
     * @notice Cancels an order
     * @param orderIndex The order index
     */
    function _cancelOrder(uint256 orderIndex) private {
        Order storage order = orders[orderIndex];

        // Cancel in LOP
        I1inchLOP.Order memory lopOrder = I1inchLOP.Order({
            salt: uint256(keccak256(abi.encodePacked(block.timestamp, orderIndex))),
            makerAsset: strategy.makerAsset,
            takerAsset: strategy.takerAsset,
            maker: address(this),
            receiver: address(this),
            allowedSender: address(0),
            makingAmount: strategy.orderSize,
            takingAmount: (strategy.orderSize * order.price) / 1e18,
            offsets: 0,
            interactions: ""
        });

        lop.cancelOrder(lopOrder);

        // Update order
        order.isActive = false;

        // Update order manager
        orderManager.cancelOrder(order.orderHash);
    }

    /**
     * @notice Gets current price from oracle
     * @return price The current price
     */
    function _getCurrentPrice() private view returns (uint256 price) {
        try oracleAdapter.getLatestPrice(strategy.makerAsset) returns (IOracleAdapter.PriceData memory priceData) {
            return priceData.price;
        } catch {
            // Fallback to strategy start price
            return strategy.startPrice;
        }
    }

    /**
     * @notice Gets dynamic spacing from oracle
     * @return spacing The dynamic spacing
     */
    function _getDynamicSpacing() private view returns (uint256 spacing) {
        try oracleAdapter.calculateDynamicSpacing(strategy.makerAsset, strategy.spacing) returns (uint256 dynamicSpacing) {
            return dynamicSpacing;
        } catch {
            // Fallback to static spacing
            return strategy.spacing;
        }
    }
}

contracts/inchbyinchFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";
import "./inchbyinchBot.sol";
import "./interfaces/IOrderManager.sol";
import "./interfaces/IOracleAdapter.sol";

/**
 * @title inchbyinchFactory
 * @notice Factory contract for deploying inchbyinch bot instances
 * @dev Uses minimal proxy pattern for gas efficiency
 */
contract inchbyinchFactory is Ownable, ReentrancyGuard, Pausable {
    using Clones for address;

    // Configuration
    uint256 public constant MAX_BOTS_PER_USER = 10;
    uint256 public constant MIN_DEPOSIT = 0.01 ether;
    uint256 public constant MAX_DEPOSIT = 1000 ether;

    // State
    address public immutable botImplementation;
    address public immutable orderManager;
    address public immutable oracleAdapter;
    address public immutable lop;

    mapping(address => address[]) public userBots;
    mapping(address => bool) public authorizedTokens;
    mapping(address => uint256) public botDeploymentCount;

    // Events
    event BotDeployed(
        address indexed user,
        address indexed bot,
        uint256 indexed botIndex,
        uint256 deploymentCost
    );

    event BotUpgraded(
        address indexed user,
        address indexed oldBot,
        address indexed newBot
    );

    event TokenAuthorized(address indexed token);
    event TokenDeauthorized(address indexed token);

    event UserLimitUpdated(uint256 newLimit);
    event DepositLimitsUpdated(uint256 minDeposit, uint256 maxDeposit);

    // Errors
    error BotDeploymentFailed();
    error BotAlreadyExists();
    error BotNotFound();
    error UserLimitExceeded();
    error InvalidDeposit();
    error InsufficientDeposit();
    error ExcessiveDeposit();
    error UnauthorizedToken();
    error InvalidBotAddress();
    error BotUpgradeFailed();
    error ZeroAddress();
    error ZeroAmount();

    // Modifiers
    modifier validAddress(address addr) {
        if (addr == address(0)) revert ZeroAddress();
        _;
    }

    modifier validAmount(uint256 amount) {
        if (amount == 0) revert ZeroAmount();
        _;
    }

    modifier validDeposit(uint256 amount) {
        if (amount < MIN_DEPOSIT) revert InsufficientDeposit();
        if (amount > MAX_DEPOSIT) revert ExcessiveDeposit();
        _;
    }

    modifier tokenAuthorized(address token) {
        if (!authorizedTokens[token]) revert UnauthorizedToken();
        _;
    }

    constructor(
        address _botImplementation,
        address _orderManager,
        address _oracleAdapter,
        address _lop
    ) Ownable(msg.sender) validAddress(_botImplementation) validAddress(_orderManager) validAddress(_oracleAdapter) validAddress(_lop) {
        botImplementation = _botImplementation;
        orderManager = _orderManager;
        oracleAdapter = _oracleAdapter;
        lop = _lop;
    }

    /**
     * @notice Deploys a new bot for a user
     * @param user The user address
     * @return bot The deployed bot address
     */
    function deployBot(address user) external payable validAddress(user) validDeposit(msg.value) returns (address bot) {
        // Check user limit
        if (userBots[user].length >= MAX_BOTS_PER_USER) revert UserLimitExceeded();

        // Deploy bot using minimal proxy
        bot = Clones.clone(botImplementation);

        // Initialize the bot
        inchbyinchBot(bot).initialize(lop, orderManager, oracleAdapter);

        // Note: Bot ownership remains with factory for security
        // User can interact through factory functions

        // Note: Bot authorization should be done by the owner of OrderManager
        // This is typically done during deployment or by admin

        // Store bot
        userBots[user].push(bot);
        botDeploymentCount[user]++;

        emit BotDeployed(user, bot, userBots[user].length - 1, msg.value);

        return bot;
    }

    /**
     * @notice Deploys multiple bots for a user
     * @param user The user address
     * @param count The number of bots to deploy
     * @return bots Array of deployed bot addresses
     */
    function deployMultipleBots(
        address user,
        uint256 count
    ) external payable validAddress(user) returns (address[] memory bots) {
        if (count == 0 || count > 5) revert ZeroAmount();
        if (userBots[user].length + count > MAX_BOTS_PER_USER) revert UserLimitExceeded();

        uint256 requiredDeposit = MIN_DEPOSIT * count;
        if (msg.value < requiredDeposit) revert InsufficientDeposit();
        if (msg.value > MAX_DEPOSIT * count) revert ExcessiveDeposit();

        bots = new address[](count);

        for (uint256 i = 0; i < count; i++) {
            address bot = Clones.clone(botImplementation);

            // Initialize the bot
            inchbyinchBot(bot).initialize(lop, orderManager, oracleAdapter);

            // Note: Bot ownership remains with factory for security

            // Note: Bot authorization should be done by the owner of OrderManager

            // Store bot
            userBots[user].push(bot);
            botDeploymentCount[user]++;

            bots[i] = bot;

            emit BotDeployed(user, bot, userBots[user].length - 1, MIN_DEPOSIT);
        }

        return bots;
    }

    /**
     * @notice Upgrades a user's bot
     * @param user The user address
     * @param botIndex The index of the bot to upgrade
     * @return newBot The new bot address
     */
    function upgradeBot(
        address user,
        uint256 botIndex
    ) external payable validAddress(user) validDeposit(msg.value) returns (address newBot) {
        address[] storage bots = userBots[user];
        if (botIndex >= bots.length) revert BotNotFound();

        address oldBot = bots[botIndex];
        if (oldBot == address(0)) revert BotNotFound();

        // Deploy new bot
        newBot = Clones.clone(botImplementation);

        // Initialize the new bot
        inchbyinchBot(newBot).initialize(lop, orderManager, oracleAdapter);

        // Note: Bot ownership remains with factory for security

        // Note: Bot authorization should be done by the owner of OrderManager

        // Replace old bot
        bots[botIndex] = newBot;

        emit BotUpgraded(user, oldBot, newBot);

        return newBot;
    }

    /**
     * @notice Gets all bots for a user
     * @param user The user address
     * @return bots Array of bot addresses
     */
    function getUserBots(address user) external view validAddress(user) returns (address[] memory bots) {
        return userBots[user];
    }

    /**
     * @notice Gets a specific bot for a user
     * @param user The user address
     * @param botIndex The bot index
     * @return bot The bot address
     */
    function getUserBot(address user, uint256 botIndex) external view validAddress(user) returns (address bot) {
        address[] storage bots = userBots[user];
        if (botIndex >= bots.length) revert BotNotFound();
        return bots[botIndex];
    }

    /**
     * @notice Gets the number of bots for a user
     * @param user The user address
     * @return count The number of bots
     */
    function getUserBotCount(address user) external view validAddress(user) returns (uint256 count) {
        return userBots[user].length;
    }

    /**
     * @notice Gets the deployment count for a user
     * @param user The user address
     * @return count The deployment count
     */
    function getUserDeploymentCount(address user) external view validAddress(user) returns (uint256 count) {
        return botDeploymentCount[user];
    }

    /**
     * @notice Authorizes a token for bot deployment
     * @param token The token address
     */
    function authorizeToken(address token) external onlyOwner validAddress(token) {
        authorizedTokens[token] = true;
        emit TokenAuthorized(token);
    }

    /**
     * @notice Deauthorizes a token
     * @param token The token address
     */
    function deauthorizeToken(address token) external onlyOwner validAddress(token) {
        authorizedTokens[token] = false;
        emit TokenDeauthorized(token);
    }

    /**
     * @notice Checks if a token is authorized
     * @param token The token address
     * @return isAuthorized Whether the token is authorized
     */
    function isTokenAuthorized(address token) external view validAddress(token) returns (bool isAuthorized) {
        return authorizedTokens[token];
    }

    /**
     * @notice Gets bot implementation address
     * @return implementation The implementation address
     */
    function getBotImplementation() external view returns (address implementation) {
        return botImplementation;
    }

    /**
     * @notice Gets factory configuration
     * @return _orderManager Order manager address
     * @return _oracleAdapter Oracle adapter address
     * @return _lop LOP address
     * @return _maxBotsPerUser Maximum bots per user
     * @return _minDeposit Minimum deposit
     * @return _maxDeposit Maximum deposit
     */
    function getFactoryConfig() external view returns (
        address _orderManager,
        address _oracleAdapter,
        address _lop,
        uint256 _maxBotsPerUser,
        uint256 _minDeposit,
        uint256 _maxDeposit
    ) {
        return (
            orderManager,
            oracleAdapter,
            lop,
            MAX_BOTS_PER_USER,
            MIN_DEPOSIT,
            MAX_DEPOSIT
        );
    }

    /**
     * @notice Withdraws ETH from the factory
     * @param amount The amount to withdraw
     */
    function withdrawETH(uint256 amount) external onlyOwner validAmount(amount) {
        require(amount <= address(this).balance, "Insufficient balance");

        (bool success, ) = payable(owner()).call{value: amount}("");
        require(success, "Transfer failed");
    }

    /**
     * @notice Withdraws tokens from the factory
     * @param token The token address
     * @param amount The amount to withdraw
     */
    function withdrawTokens(address token, uint256 amount) external onlyOwner validAddress(token) validAmount(amount) {
        // Use low-level call for token transfer
        (bool success, ) = token.call(abi.encodeWithSignature("transfer(address,uint256)", owner(), amount));
        require(success, "Transfer failed");
    }

    /**
     * @notice Pauses the factory
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice Unpauses the factory
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @notice Emergency function to recover stuck tokens
     * @param token The token address
     * @param to The recipient address
     * @param amount The amount to recover
     */
    function emergencyRecover(address token, address to, uint256 amount) external onlyOwner validAddress(token) validAddress(to) validAmount(amount) {
        // Use low-level call for token transfer
        (bool success, ) = token.call(abi.encodeWithSignature("transfer(address,uint256)", to, amount));
        require(success, "Transfer failed");
    }

    /**
     * @notice Receives ETH
     */
    receive() external payable {
        // Accept ETH deposits
    }

    /**
     * @notice Fallback function
     */
    fallback() external payable {
        // Accept ETH deposits
    }
}

contracts/interfaces/I1inchLOP.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title I1inchLOP
 * @notice Interface for 1inch Limit Order Protocol
 * @dev Based on the official 1inch LOP contract interface
 */
interface I1inchLOP {
    struct Order {
        uint256 salt;
        address makerAsset;
        address takerAsset;
        address maker;
        address receiver;
        address allowedSender;
        uint256 makingAmount;
        uint256 takingAmount;
        uint256 offsets;
        bytes interactions;
    }

    struct OrderRFQ {
        uint256 info;
        address makerAsset;
        address takerAsset;
        address maker;
        address allowedSender;
        uint256 makingAmount;
        uint256 takingAmount;
    }

    event OrderFilled(
        bytes32 indexed orderHash,
        address indexed maker,
        address indexed taker,
        uint256 makingAmount,
        uint256 takingAmount,
        uint256 remainingAmount
    );

    event OrderCanceled(
        bytes32 indexed orderHash,
        address indexed maker
    );

    /**
     * @notice Places a limit order
     * @param order The order structure
     * @param signature The signature for the order
     * @param interaction The interaction data for custom callbacks
     * @return makingAmount The amount of maker asset that was filled
     * @return takingAmount The amount of taker asset that was filled
     */
    function fillOrder(
        Order calldata order,
        bytes calldata signature,
        bytes calldata interaction
    ) external payable returns (uint256 makingAmount, uint256 takingAmount);

    /**
     * @notice Cancels an order
     * @param order The order to cancel
     */
    function cancelOrder(Order calldata order) external;

    /**
     * @notice Checks if an order is valid
     * @param order The order to check
     * @return isValid Whether the order is valid
     */
    function checkOrder(Order calldata order) external view returns (bool isValid);

    /**
     * @notice Gets the order hash
     * @param order The order
     * @return orderHash The hash of the order
     */
    function getOrderHash(Order calldata order) external view returns (bytes32 orderHash);

    /**
     * @notice Gets the remaining amount for an order
     * @param order The order
     * @return remainingAmount The remaining amount
     */
    function remaining(Order calldata order) external view returns (uint256 remainingAmount);
}

contracts/interfaces/IOracleAdapter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IOracleAdapter
 * @notice Interface for OracleAdapter contract
 */
interface IOracleAdapter {
    struct PriceData {
        uint256 price;
        uint256 timestamp;
        uint256 volatility;
        bool isValid;
    }

    struct VolatilityConfig {
        uint256 baseSpacing;
        uint256 volatilityMultiplier;
        uint256 minSpacing;
        uint256 maxSpacing;
    }

    event PriceUpdated(
        address indexed asset,
        uint256 indexed price,
        uint256 indexed timestamp,
        uint256 volatility
    );

    event VolatilityConfigUpdated(
        address indexed asset,
        uint256 baseSpacing,
        uint256 volatilityMultiplier
    );

    /**
     * @notice Gets the latest price for an asset
     * @param asset The asset address
     * @return priceData The price data
     */
    function getLatestPrice(address asset) external view returns (PriceData memory priceData);

    /**
     * @notice Gets the price at a specific timestamp
     * @param asset The asset address
     * @param timestamp The timestamp
     * @return price The price at the timestamp
     */
    function getPriceAt(address asset, uint256 timestamp) external view returns (uint256 price);

    /**
     * @notice Gets the volatility for an asset
     * @param asset The asset address
     * @param timeframe The timeframe in seconds
     * @return volatility The volatility value
     */
    function getVolatility(address asset, uint256 timeframe) external view returns (uint256 volatility);

    /**
     * @notice Calculates dynamic spacing based on volatility
     * @param asset The asset address
     * @param baseSpacing The base spacing
     * @return dynamicSpacing The calculated dynamic spacing
     */
    function calculateDynamicSpacing(
        address asset,
        uint256 baseSpacing
    ) external view returns (uint256 dynamicSpacing);

    /**
     * @notice Updates price data for an asset
     * @param asset The asset address
     * @param price The new price
     * @param timestamp The timestamp
     */
    function updatePrice(address asset, uint256 price, uint256 timestamp) external;

    /**
     * @notice Sets volatility configuration for an asset
     * @param asset The asset address
     * @param config The volatility configuration
     */
    function setVolatilityConfig(address asset, VolatilityConfig calldata config) external;

    /**
     * @notice Gets volatility configuration for an asset
     * @param asset The asset address
     * @return config The volatility configuration
     */
    function getVolatilityConfig(address asset) external view returns (VolatilityConfig memory config);

    /**
     * @notice Checks if price data is stale
     * @param asset The asset address
     * @param maxAge The maximum age in seconds
     * @return isStale Whether the price data is stale
     */
    function isPriceStale(address asset, uint256 maxAge) external view returns (bool isStale);

    /**
     * @notice Gets the price change percentage over a timeframe
     * @param asset The asset address
     * @param timeframe The timeframe in seconds
     * @return changePercent The price change percentage
     */
    function getPriceChangePercent(address asset, uint256 timeframe) external view returns (int256 changePercent);
}

contracts/interfaces/IOrderManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IOrderManager
 * @notice Interface for OrderManager contract
 */
interface IOrderManager {
    struct OrderInfo {
        bytes32 orderHash;
        address bot;
        address makerAsset;
        address takerAsset;
        uint256 makingAmount;
        uint256 takingAmount;
        uint256 remainingAmount;
        uint256 orderIndex;
        bool isActive;
        uint256 createdAt;
        uint256 lastUpdated;
    }

    struct StrategyInfo {
        address bot;
        address owner;
        uint256 totalOrders;
        uint256 activeOrders;
        uint256 totalFilled;
        uint256 strategyType; // 0: Buy Ladder, 1: Sell Ladder, 2: Buy+Sell
        bool isActive;
        uint256 createdAt;
        uint256 lastUpdated;
    }

    event OrderRegistered(
        bytes32 indexed orderHash,
        address indexed bot,
        uint256 indexed orderIndex,
        address makerAsset,
        address takerAsset,
        uint256 makingAmount,
        uint256 takingAmount
    );

    event OrderFilled(
        bytes32 indexed orderHash,
        address indexed bot,
        uint256 indexed orderIndex,
        uint256 filledAmount,
        uint256 remainingAmount
    );

    event OrderCanceled(
        bytes32 indexed orderHash,
        address indexed bot,
        uint256 indexed orderIndex
    );

    event StrategyCreated(
        address indexed bot,
        address indexed owner,
        uint256 indexed strategyType
    );

    event StrategyUpdated(
        address indexed bot,
        uint256 totalOrders,
        uint256 activeOrders
    );

    /**
     * @notice Registers a new order
     * @param orderHash The hash of the order
     * @param bot The bot address
     * @param makerAsset The maker asset address
     * @param takerAsset The taker asset address
     * @param makingAmount The making amount
     * @param takingAmount The taking amount
     * @return orderIndex The index of the registered order
     */
    function registerOrder(
        bytes32 orderHash,
        address bot,
        address makerAsset,
        address takerAsset,
        uint256 makingAmount,
        uint256 takingAmount
    ) external returns (uint256 orderIndex);

    /**
     * @notice Updates order fill status
     * @param orderHash The hash of the order
     * @param filledAmount The amount that was filled
     * @param remainingAmount The remaining amount
     */
    function updateOrderFill(
        bytes32 orderHash,
        uint256 filledAmount,
        uint256 remainingAmount
    ) external;

    /**
     * @notice Cancels an order
     * @param orderHash The hash of the order
     */
    function cancelOrder(bytes32 orderHash) external;

    /**
     * @notice Creates a new strategy
     * @param bot The bot address
     * @param owner The owner address
     * @param strategyType The type of strategy
     */
    function createStrategy(
        address bot,
        address owner,
        uint256 strategyType
    ) external;

    /**
     * @notice Updates strategy statistics
     * @param bot The bot address
     * @param totalOrders The total number of orders
     * @param activeOrders The number of active orders
     */
    function updateStrategy(
        address bot,
        uint256 totalOrders,
        uint256 activeOrders
    ) external;

    /**
     * @notice Gets order information
     * @param orderHash The hash of the order
     * @return orderInfo The order information
     */
    function getOrderInfo(bytes32 orderHash) external view returns (OrderInfo memory orderInfo);

    /**
     * @notice Gets strategy information
     * @param bot The bot address
     * @return strategyInfo The strategy information
     */
    function getStrategyInfo(address bot) external view returns (StrategyInfo memory strategyInfo);

    /**
     * @notice Gets all orders for a bot
     * @param bot The bot address
     * @return orderHashes Array of order hashes
     */
    function getBotOrders(address bot) external view returns (bytes32[] memory orderHashes);

    /**
     * @notice Gets active orders for a bot
     * @param bot The bot address
     * @return orderHashes Array of active order hashes
     */
    function getBotActiveOrders(address bot) external view returns (bytes32[] memory orderHashes);

    /**
     * @notice Checks if a bot is authorized
     * @param bot The bot address
     * @return isAuthorized Whether the bot is authorized
     */
    function isBotAuthorized(address bot) external view returns (bool isAuthorized);

    /**
     * @notice Authorizes a bot to register orders
     * @param bot The bot address to authorize
     */
    function authorizeBot(address bot) external;
}

